        return new Source("{% extends 'base.html.twig' %}

{% block title %}Lista przepisów{% endblock %}

{% block stylesheets %}
    {{ encore_entry_link_tags('app') }}
    <!-- Link do Bootstrap CSS -->
    <link href=\"https://cdn.jsdelivr.net/npm/bootstrap@5.3.0-alpha1/dist/css/bootstrap.min.css\" rel=\"stylesheet\">
{% endblock %}

{% block body %}
    <div class=\"container mt-4\">
        <h1 class=\"mb-4\">Przepisy</h1>

        <!-- Wyświetlanie przepisów -->
        <div class=\"row\">
            {% for recipe in recipes %}
                <div class=\"col-md-4 mb-4\">
                    <div class=\"card\">
                        <img src=\"{{ recipe.image }}\" alt=\"{{ recipe.name }}\" class=\"card-img-top\" style=\"height: 200px; object-fit: cover;\">
                        <div class=\"card-body\">
                            <h5 class=\"card-title\">{{ recipe.name }}</h5>
                            <p class=\"card-text\">{{ recipe.description }}</p>
                            <p><strong>Składniki:</strong> {{ recipe.ingredients }}</p>
                        </div>
                    </div>
                </div>
            {% else %}
                <p>Brak przepisów spełniających kryteria.</p>
            {% endfor %}
        </div>
    </div>
{% endblock %}", "recipe/index.html.twig", "E:\\DariaR\\IQuchnia\\wersja_3\\iquchnia\\templates\\recipe\\index.html.twig");
